mapper.select:
	select *
from
(select rownum as rn, b_id, b_sortation, b_area, b_addr,
b_name , b_time ,
b_service , b_mapdata, b_manager , b_managernumber ,
b_branchname,
b_branchnumber ,b_cr ,b_email,b_file
from(
select * from
branch where
b_branchname like '%'||#{branchnameInput}||'%'
 and b_area = #{branchareaInput} 
)
)  where rn >= #{start} and rn <= #{end}

select * from branch where 
b_branchname like '%'||#{branchnameInput}||'%'
 and b_area = #{branchareaInput}

mapper.update:
update branch
set
b_sortation = #{b_sortation}, b_area = #{b_area}, b_addr = #{b_addr}, b_name = #{b_name}, b_time = #{b_time}, b_service = #{b_service}, b_mapdata = #{b_mapdata}, b_manager = #{b_manager}, b_managernumber = #{b_managernumber}, b_branchname = #{b_branchname}, b_branchnumber = #{b_branchnumber}, b_cr = #{b_cr}, b_email = #{b_email}, b_file = #{b_file}
where b_id = #{b_id}
