mapper.select:
select * from ( select rownum as rn,u_no, u_id,u_logintype,i_no,i_name,i_phonenum,i_newdate,i_grade,i_email, mc_number,mc_id,mc_model,mc_brand,mc_year from ( select * from auth_user u inner join auth_userinfo ui on u.u_no = ui.i_no left OUTER JOIN my_car mc on u.u_no = mc.mc_id where
u.u_id = #{u_id} and
ui.i_name like '%'||#{i_name}||'%'
and mc.mc_number = #{mc_number}
and ui.i_grade = #{i_grade}
order by ui.i_newdate desc) )where rn >= #{start} and rn <= #{end}
