mapper.update:
update tire_group
set tg_print = #{tg_print}
where tg_id = #{tg_id}

update tire_group
set tg_sedan = #{tg_sedan}
where tg_id = #{tg_id}

update tire_group
set tg_suv = #{tg_suv}
where tg_id = #{tg_id}

mapper.select:
